  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}><MDXTag name="h1" components={components} props={{"id":"overview"}}>{`Overview`}</MDXTag>
<MDXTag name="p" components={components}>{`The Pando Network rely on three different components.`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="strong" components={components} parentName="li">{`Organisms`}</MDXTag>{`. Organisms are pando-based content DAOs - software, books, articles, songs, etc. -  living on the blockchain as individuals of their own. You can think about these organisms as a pando equivalent of git remote repositories.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="strong" components={components} parentName="li">{`Factories`}</MDXTag>{`. Factories allow user to locally extract, modify and submit updates to pando-hosted contents. You can think of these factories as a pando equivalent of git local repositories.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="strong" components={components} parentName="li">{`Nodes`}</MDXTag>{`. Nodes are computer indexing the content of pando's public organisms to offer searchability, cross-organism reputation system, etc. The software run by these nodes is still being developed and is not released yet.`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript","metastring":""}}>{`[{
